import React, { useState, useEffect } from 'react';
import './Schools.css';
import { apiService } from '../services/api';

interface SchoolsProps {
  onSelectSchool: (schoolName: string) => void;
}

interface School {
  id: number;
  name: string;
  image: string;
  location: string;
  isOtras?: boolean;
}

const Schools: React.FC<SchoolsProps> = ({ onSelectSchool }) => {
  const [schools, setSchools] = useState<School[]>([]);
  const [loading, setLoading] = useState(true);

  const loadSchools = async () => {
    try {
      setLoading(true);
      const folders = await apiService.getFolders();

      // Mapear carpetas del backend a escuelas
      const schoolsFromFolders: School[] = folders.map((folder: any, index: number) => ({
        id: index + 1,
        name: folder.name,
        image: getSchoolImage(folder.name),
        location: 'Lanzarote',
        isOtras: false
      }));

      // Agregar "OTRAS ESCUELAS" al final
      schoolsFromFolders.push({
        id: schoolsFromFolders.length + 1,
        name: 'OTRAS ESCUELAS',
        image: '/OTRAS.jpg',
        location: 'Varias',
        isOtras: true
      });

      setSchools(schoolsFromFolders);
    } catch (error) {
      console.error('Error loading schools:', error);
      // Fallback a escuelas estáticas si falla
      setSchools([
        {
          id: 1,
          name: 'REDSTAR',
          image: '/REDSTAR.JPG',
          location: 'Lanzarote',
        },
        {
          id: 2,
          name: 'SANTA SURF PROCENTER',
          image: '/SANTASURF-PROCENTER.JPG',
          location: 'Lanzarote',
        },
        {
          id: 3,
          name: 'LANZAROTE SURF SCHOOL',
          image: '/LANZAROTE.jpg',
          location: 'Lanzarote',
        },
        {
          id: 4,
          name: 'ZOOPARK',
          image: '/ZOOPARK.jpg',
          location: 'Lanzarote',
        },
        {
          id: 5,
          name: 'MAURI SURF',
          image: '/MAURI-SURF.JPG',
          location: 'Lanzarote',
        },
        {
          id: 6,
          name: 'JMC SURFTRAINING',
          image: '/JMC-SURFTRAINING.jpg',
          location: 'Lanzarote',
        },
        {
          id: 7,
          name: 'VOLCANO',
          image: '/VOLCANO.jpg',
          location: 'Lanzarote',
        },
        {
          id: 8,
          name: 'OTRAS ESCUELAS',
          image: '/OTRAS.jpg',
          location: 'Varias',
          isOtras: true,
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadSchools();
  }, []);

  const getSchoolImage = (folderName: string): string => {
    // Mapear nombres de carpetas a imágenes
    const imageMap: { [key: string]: string } = {
      'REDSTAR': '/REDSTAR.JPG',
      'SANTA SURF PROCENTER': '/SANTASURF-PROCENTER.JPG',
      'LANZAROTE SURF SCHOOL': '/LANZAROTE.jpg',
      'ZOOPARK': '/ZOOPARK.jpg',
      'MAURI SURF': '/MAURI-SURF.JPG',
      'JMC SURFTRAINING': '/JMC-SURFTRAINING.jpg',
      'VOLCANO': '/VOLCANO.jpg',
      'Surf': '/REDSTAR.JPG', // Carpeta de ejemplo
    };

    return imageMap[folderName] || '/OTRAS.jpg';
  };

  if (loading) {
    return (
      <div className="schools-page">
        <div style={{ textAlign: 'center', padding: '4rem' }}>
          <div className="loading-spinner"></div>
          <p>Cargando escuelas...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="schools-page">
      {/* Sección de Escuelas */}
      <section className="schools-section-main">
        <div className="section-wrapper">
          <div className="schools-header-content">
            <h2 className="schools-intro-title">Tu escuela, tu día, tus fotos.</h2>
            <div className="schools-description">
              <p className="description-main">Selecciona la escuela con la que hiciste tu clase de surf.</p>
              <p className="description-secondary">
                Cada escuela tiene su propio espacio dentro de la web, donde encontrarás tus fotos organizadas por fecha.
              </p>
            </div>
          </div>
          <div className="schools-grid">
            {schools.map((school) => (
              <div 
                key={school.id} 
                className={`school-card ${school.isOtras ? 'school-card-otras' : ''}`}
                onClick={() => onSelectSchool(school.name)}
              >
                <div className="school-image-wrapper">
                  <img 
                    src={school.image} 
                    alt={school.name}
                    className="school-image"
                  />
                  <div className="school-overlay"></div>
                </div>
                {school.isOtras ? (
                  <h3 className="school-name school-name-otras">
                    <span className="otras-line">OTRAS</span>
                    <span className="otras-line">ESCUELAS</span>
                  </h3>
                ) : (
                  <h3 className="school-name">{school.name}</h3>
                )}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Mensaje de Valor */}
      <section className="value-section">
        <div className="value-content">
          <p className="value-text">
            <span className="value-item">Fotos en alta calidad</span>
            <span className="value-separator">·</span>
            <span className="value-item">Descarga inmediata</span>
            <span className="value-separator">·</span>
            <span className="value-item">Pago seguro</span>
          </p>
        </div>
      </section>
    </div>
  );
};

export default Schools;
