import React from 'react';
import './Schools.css';

interface SchoolsProps {
  onSelectSchool: (schoolName: string) => void;
}

const Schools: React.FC<SchoolsProps> = ({ onSelectSchool }) => {
  const schools = [
    {
      id: 1,
      name: 'REDSTAR',
      image: '/REDSTAR.JPG',
      location: 'Lanzarote',
      badge: '‚≠ê',
    },
    {
      id: 2,
      name: 'SANTA SURF PROCENTER',
      image: '/SANTASURF-PROCENTER.JPG',
      location: 'Lanzarote',
      badge: 'üéØ',
    },
    {
      id: 3,
      name: 'LANZAROTE SURF SCHOOL',
      image: '/LANZAROTE.jpg',
      location: 'Lanzarote',
      badge: 'üåä',
    },
    {
      id: 4,
      name: 'ZOOPARK',
      image: '/ZOOPARK.jpg',
      location: 'Lanzarote',
      badge: 'ü¶Å',
    },
    {
      id: 5,
      name: 'MAURI SURF',
      image: '/MAURI-SURF.JPG',
      location: 'Lanzarote',
      badge: 'üèÑ',
    },
    {
      id: 6,
      name: 'JMC SURFTRAINING',
      image: '/JMC-SURFTRAINING.jpg',
      location: 'Lanzarote',
      badge: 'üèÜ',
    },
    {
      id: 7,
      name: 'VOLCANO',
      image: '/VOLCANO.jpg',
      location: 'Lanzarote',
      badge: 'üåã',
    },
    {
      id: 8,
      name: 'OTRAS',
      image: '/OTRAS.jpg',
      location: 'Varias',
      badge: '‚ú®',
    },
  ];

  return (
    <div className="schools-page">
      <div className="schools-header">
        <div className="schools-text-block">
          <h1 className="schools-title">Tu escuela, tu d√≠a, tus recuerdos</h1>
          <div className="schools-description">
            <p className="description-main">Selecciona la escuela con la que hiciste tu clase de surf.</p>
            <p className="description-secondary">
              Cada escuela tiene su propio espacio dentro de la web, donde encontrar√°s tus fotos organizadas por fecha.
            </p>
          </div>
        </div>
      </div>

      <div className="schools-container">
        <div className="schools-grid">
          {schools.map((school) => (
            <div 
              key={school.id} 
              className="school-card"
              onClick={() => onSelectSchool(school.name)}
            >
              <div className="school-badge">{school.badge}</div>
              <div className="school-image-wrapper">
                <img 
                  src={school.image} 
                  alt={school.name}
                  className="school-image"
                />
                <div className="school-overlay">
                  <div className="overlay-content">
                    <span className="view-photos-text">Ver fotos</span>
                    <span className="arrow-icon">‚Üí</span>
                  </div>
                </div>
              </div>
              <div className="school-info">
                <h3 className="school-name">{school.name}</h3>
                <div className="school-location">
                  <span className="location-icon">üìç</span>
                  <span className="location-text">{school.location}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Secci√≥n de ayuda */}
      <div className="schools-help">
        <div className="help-card">
          <span className="help-icon">‚ùì</span>
          <h3 className="help-title">¬øNo encuentras tu escuela?</h3>
          <p className="help-text">Selecciona "OTRAS" y podr√°s buscar tus fotos igualmente</p>
        </div>
      </div>
    </div>
  );
};

export default Schools;
