import React, { useState, useRef } from 'react';
import './Recognition.css';
import apiService, { Photo } from '../services/api';

interface RecognitionProps {
  onAddToCart: (item: any) => void;
}

interface AnalysisResult {
  status: string;
  matches: Array<{ file: string; similarity: number }>;
  non_matches: Array<{ file: string; similarity: number }>;
  statistics: {
    total_photos: number;
    matches_count: number;
    match_percentage: number;
    threshold_used: number;
  };
}

const Recognition: React.FC<RecognitionProps> = ({ onAddToCart }) => {
  const [activeTab, setActiveTab] = useState<'camera' | 'upload'>('camera');
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [uploadedImage, setUploadedImage] = useState<string | null>(null);
  const [uploadedFile, setUploadedFile] = useState<File | null>(null);
  const [isSearching, setIsSearching] = useState(false);
  const [analysisResult, setAnalysisResult] = useState<AnalysisResult | null>(null);
  const [showResults, setShowResults] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setUploadedFile(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target?.result as string);
      };
      reader.readAsDataURL(file);
      setError(null);
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const handleAnalyzePhoto = async () => {
    if (!uploadedFile) {
      setError('Por favor, selecciona una foto primero');
      return;
    }

    setIsSearching(true);
    setError(null);
    setShowResults(false);

    try {
      const formData = new FormData();
      formData.append('selfie', uploadedFile);

      const response = await fetch('http://localhost:8000/compare-faces-folder?search_folder=Surf', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Error en la búsqueda');
      }

      const data: AnalysisResult = await response.json();
      setAnalysisResult(data);
      setShowResults(true);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Error al analizar la foto';
      setError(errorMessage);
      setShowResults(false);
    } finally {
      setIsSearching(false);
    }
  };

  const handleAddToCart = (filename: string) => {
    const cartItem = {
      id: `Surf_${filename}`,
      name: `Surf - ${filename}`,
      price: 29.99,
      image: `http://localhost:8000/photos/preview?folder_name=Surf&filename=${filename}&watermark=true`,
    };
    onAddToCart(cartItem);
  };

  return (
    <section className="recognition" id="recognition">
      <div className="section-wrapper">
        <div className="section-header">
          <h2>Reconocimiento Facial Inteligente</h2>
          <p>Captura tu rostro e identifica instantáneamente todas las fotos donde apareces</p>
        </div>
        <div className="recognition-grid">
          <div className="camera-panel">
            <div className="tab-buttons">
              <button 
                className={`tab-btn ${activeTab === 'camera' ? 'active' : ''}`}
                onClick={() => setActiveTab('camera')}
              >
                Cámara
              </button>
              <button 
                className={`tab-btn ${activeTab === 'upload' ? 'active' : ''}`}
                onClick={() => setActiveTab('upload')}
              >
                Subir Foto
              </button>
            </div>

            {activeTab === 'camera' && (
              <div className="tab-content">
                <h3>Captura Tu Rostro</h3>
                <div className="camera-feed">
                  <video id="video" autoPlay playsInline></video>
                </div>
                <div className="button-group">
                  <button className="btn btn-dark">Iniciar</button>
                  <button className="btn btn-success">Capturar</button>
                  <button className="btn btn-danger">Detener</button>
                </div>
              </div>
            )}

            {activeTab === 'upload' && (
              <div className="tab-content">
                <h3>Sube Tu Foto</h3>
                <div className="upload-area">
                  {uploadedImage ? (
                    <div className="uploaded-image-container">
                      <img src={uploadedImage} alt="Foto subida" className="uploaded-image" />
                      <button 
                        className="btn btn-secondary"
                        onClick={() => {
                          setUploadedImage(null);
                          if (fileInputRef.current) fileInputRef.current.value = '';
                        }}
                      >
                        Cambiar Foto
                      </button>
                    </div>
                  ) : (
                    <div className="upload-placeholder" onClick={handleUploadClick}>
                      <div className="upload-icon">↑</div>
                      <p>Haz clic para seleccionar una foto</p>
                      <span>o arrastra una imagen aquí</span>
                    </div>
                  )}
                </div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleFileUpload}
                  style={{ display: 'none' }}
                />
                {uploadedImage && (
                  <div className="button-group">
                    <button 
                      className="btn btn-success"
                      onClick={handleAnalyzePhoto}
                      disabled={isSearching}
                    >
                      {isSearching ? 'Analizando...' : 'Analizar Foto'}
                    </button>
                    <button 
                      className="btn btn-dark"
                      onClick={() => {
                        setUploadedImage(null);
                        setUploadedFile(null);
                        if (fileInputRef.current) fileInputRef.current.value = '';
                      }}
                    >
                      Limpiar
                    </button>
                  </div>
                )}
                {error && (
                  <div className="error-message">
                    {error}
                  </div>
                )}
              </div>
            )}
          </div>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-number">01</div>
              <h4>Reconocimiento Avanzado</h4>
              <p>Tecnología de IA de última generación</p>
            </div>
            <div className="feature-card">
              <div className="feature-number">02</div>
              <h4>Coincidencia Precisa</h4>
              <p>Filtra automáticamente tus fotos</p>
            </div>
            <div className="feature-card">
              <div className="feature-number">03</div>
              <h4>Compra Instantánea</h4>
              <p>Compra en segundos</p>
            </div>
          </div>
        </div>

        {showResults && (
          <div className="results-section">
            <h3>Resultados de Búsqueda</h3>
            {searchResults.length > 0 ? (
              <div className="results-grid">
                {searchResults.map((photo) => (
                  <div key={photo.id} className="result-card">
                    <img src={photo.thumbnail} alt={photo.filename} className="result-image" />
                    <div className="result-info">
                      <p className="result-school">{photo.school}</p>
                      <p className="result-date">{photo.date}</p>
                      {photo.similarity !== undefined && (
                        <p className="result-similarity">
                          Similitud: {photo.similarity.toFixed(2)}%
                        </p>
                      )}
                      <button 
                        className="btn btn-primary"
                        onClick={() => handleAddToCart(photo)}
                      >
                        Agregar al Carrito
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="no-results">No se encontraron fotos similares</p>
            )}
          </div>
        )}
      </div>
    </section>
  );
};

export default Recognition;
