import React, { useState, useEffect } from 'react';
import './Navbar.css';

interface NavbarProps {
  cartCount: number;
  onCartClick: () => void;
}

const Navbar: React.FC<NavbarProps> = ({ cartCount, onCartClick }) => {
  const [activeSection, setActiveSection] = useState<string>('schools');
  const [currentLanguage, setCurrentLanguage] = useState<'es' | 'en'>('es');
  const [showLanguageMenu, setShowLanguageMenu] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['schools', 'how-it-works', 'sobre-mi', 'pricing', 'faqs'];

      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 150 && rect.bottom >= 150) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Cerrar men칰 de idiomas al hacer clic fuera
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as HTMLElement;
      if (!target.closest('.language-selector')) {
        setShowLanguageMenu(false);
      }
    };

    if (showLanguageMenu) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showLanguageMenu]);

  const handleLogoClick = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    setActiveSection('schools');
  };

  const handleLanguageChange = (lang: 'es' | 'en') => {
    setCurrentLanguage(lang);
    setShowLanguageMenu(false);
    // Aqu칤 puedes a침adir la l칩gica para cambiar el idioma de toda la aplicaci칩n
    console.log(`Idioma cambiado a: ${lang}`);
  };

  const navItems = [
    { id: 'schools', label: 'Escuelas', href: '#schools' },
    { id: 'how-it-works', label: 'C칩mo funciona', href: '#how-it-works' },
    { id: 'sobre-mi', label: 'Sobre m칤', href: '#sobre-mi' },
    { id: 'pricing', label: 'Precios', href: '#pricing' },
    { id: 'faqs', label: 'FAQs', href: '#faqs' },
  ];

  const languages = [
    { code: 'es', label: 'Espa침ol', flag: '游쀯릖' },
    { code: 'en', label: 'English', flag: '游섫릖' },
  ];

  return (
    <nav className="navbar">
      <div className="navbar-wrapper">
        <div className="navbar-brand" onClick={handleLogoClick}>
          <img src="/image.png" alt="Logo" className="brand-logo" />
        </div>

        <ul className="navbar-menu">
          {navItems.map((item) => (
            <li key={item.id}>
              <a 
                href={item.href} 
                className={`nav-link ${activeSection === item.id ? 'active' : ''}`}
              >
                {item.label}
              </a>
            </li>
          ))}
        </ul>

        <div className="navbar-actions">
          {/* Selector de idiomas */}
          <div className="language-selector">
            <button 
              className="language-button"
              onClick={() => setShowLanguageMenu(!showLanguageMenu)}
            >
              <span className="language-flag">
                {languages.find(lang => lang.code === currentLanguage)?.flag}
              </span>
              <span className="language-code">{currentLanguage.toUpperCase()}</span>
              <svg 
                className={`language-arrow ${showLanguageMenu ? 'open' : ''}`}
                width="12" 
                height="12" 
                viewBox="0 0 12 12" 
                fill="none"
              >
                <path d="M2 4L6 8L10 4" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
              </svg>
            </button>

            {showLanguageMenu && (
              <div className="language-menu">
                {languages.map((lang) => (
                  <button
                    key={lang.code}
                    className={`language-option ${currentLanguage === lang.code ? 'active' : ''}`}
                    onClick={() => handleLanguageChange(lang.code as 'es' | 'en')}
                  >
                    <span className="language-flag">{lang.flag}</span>
                    <span className="language-label">{lang.label}</span>
                    {currentLanguage === lang.code && (
                      <svg className="check-icon" width="16" height="16" viewBox="0 0 16 16" fill="none">
                        <path d="M3 8L6 11L13 4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      </svg>
                    )}
                  </button>
                ))}
              </div>
            )}
          </div>

          <button className="cart-button" onClick={onCartClick}>
          <svg className="cart-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="9" cy="21" r="1"></circle>
            <circle cx="20" cy="21" r="1"></circle>
            <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path>
          </svg>
            Recuerdos
            {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
