import React from 'react';
import './HowItWorks.css';

const HowItWorks: React.FC = () => {
  const steps = [
    {
      number: 1,
      title: 'Elige tu escuela de surf',
      description: 'Selecciona la escuela donde hiciste tu clase',
      icon: 'üèÑ',
    },
    {
      number: 2,
      title: 'Selecciona la fecha de tu sesi√≥n',
      description: 'Elige el d√≠a en el que surfeaste',
      icon: 'üìÖ',
    },
    {
      number: 3,
      title: 'Sube un selfie',
      description: 'La IA buscar√° tus fotos autom√°ticamente',
      icon: 'üì∏',
    },
    {
      number: 4,
      title: 'Visualiza tus fotos',
      description: 'Ve todas las fotos en las que apareces',
      icon: 'üñºÔ∏è',
    },
    {
      number: 5,
      title: 'A√±ade al carrito',
      description: 'Selecciona las fotos que deseas comprar',
      icon: 'üõí',
    },
    {
      number: 6,
      title: 'Paga de forma segura',
      description: 'Realiza el pago de manera r√°pida y segura',
      icon: 'üí≥',
    },
    {
      number: 7,
      title: 'Descarga inmediata',
      description: 'Obt√©n tus fotos al instante sin marca de agua',
      icon: '‚¨áÔ∏è',
    },
  ];

  return (
    <section className="how-it-works" id="how-it-works">
      <div className="how-it-works-wrapper">
        <div className="how-it-works-header">
          <h2 className="how-it-works-title">C√≥mo funciona</h2>
          <p className="how-it-works-subtitle">
            Consigue tus fotos de surf en cuesti√≥n de minutos.
          </p>
        </div>

        <div className="steps-container">
          {steps.map((step, index) => (
            <div key={step.number} className="step">
              <div className="step-number">{step.number}</div>

              <div className="step-content">
                <div className="step-icon">{step.icon}</div>
                <h3 className="step-title">{step.title}</h3>
                <p className="step-description">{step.description}</p>
              </div>

              {index < steps.length - 1 && (
                <div className="step-connector">
                  <div className="connector-line"></div>
                  <div className="connector-dot"></div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Nota especial sobre reconocimiento facial */}
        <div className="facial-recognition-note">
          <div className="note-icon">üîç</div>
          <div className="note-content">
            <h4>B√∫squeda inteligente</h4>
            <p>El reconocimiento facial busca solo en las fotos del d√≠a seleccionado, no en toda la web.</p>
          </div>
        </div>

        {/* CTA Button */}
        <div className="how-it-works-cta">
          <a href="#schools" className="btn btn-primary btn-large">
            Encuentra tus fotos
          </a>
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
