import React, { useState, useEffect } from 'react';
import './SchoolDays.css';

interface SchoolDaysProps {
  schoolName: string;
  onBack: () => void;
  onSelectDay: (date: string) => void;
}

const SchoolDays: React.FC<SchoolDaysProps> = ({ schoolName, onBack, onSelectDay }) => {
  const [selectedDate, setSelectedDate] = useState<string>('');
  const [heroImage, setHeroImage] = useState<string>('');

  // Mock data - en producción esto vendría del backend
  // Cargar una foto aleatoria de la escuela para el hero
  useEffect(() => {
    const fetchHeroImage = async () => {
      try {
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
        const response = await fetch(`${apiUrl}/photos/list?folder_name=${schoolName}`);

        if (response.ok) {
          const data = await response.json();
          if (data.photos && data.photos.length > 0) {
            // Seleccionar una foto aleatoria
            const randomPhoto = data.photos[Math.floor(Math.random() * data.photos.length)];
            const imageUrl = `${apiUrl}/photos/preview?folder_name=${schoolName}&filename=${randomPhoto.filename}&watermark=false`;
            setHeroImage(imageUrl);
          }
        }
      } catch (err) {
        console.error('Error cargando imagen del hero:', err);
      }
    };

    fetchHeroImage();
  }, [schoolName]);

  const [availableDays, setAvailableDays] = useState<Array<{
    date: string;
    displayDate: string;
    photoCount: number;
    thumbnailUrl: string;
  }>>([]);

  // Cargar días disponibles con fotos reales
  useEffect(() => {
    const fetchAvailableDays = async () => {
      try {
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
        const response = await fetch(`${apiUrl}/photos/list?folder_name=${schoolName}`);

        if (response.ok) {
          const data = await response.json();
          if (data.photos && data.photos.length > 0) {
            // Agrupar fotos por fecha
            const photosByDate: { [key: string]: any[] } = {};

            data.photos.forEach((photo: any) => {
              const date = photo.created_at.split('T')[0]; // Obtener solo la fecha
              if (!photosByDate[date]) {
                photosByDate[date] = [];
              }
              photosByDate[date].push(photo);
            });

            // Crear array de días disponibles
            const days = Object.keys(photosByDate).map(date => {
              const photos = photosByDate[date];
              const randomPhoto = photos[Math.floor(Math.random() * photos.length)];

              // Formatear fecha
              const dateObj = new Date(date);
              const options: Intl.DateTimeFormatOptions = { 
                day: 'numeric', 
                month: 'long', 
                year: 'numeric' 
              };
              const displayDate = dateObj.toLocaleDateString('es-ES', options);

              return {
                date,
                displayDate: displayDate.charAt(0).toUpperCase() + displayDate.slice(1),
                photoCount: photos.length,
                thumbnailUrl: `${apiUrl}/photos/preview?folder_name=${schoolName}&filename=${randomPhoto.filename}&watermark=false`
              };
            }).sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

            setAvailableDays(days);
          }
        }
      } catch (err) {
        console.error('Error cargando días disponibles:', err);
        // Fallback a datos mock si falla
        setAvailableDays([
          { 
            date: '2024-01-15', 
            displayDate: '15 de Enero, 2024', 
            photoCount: 45,
            thumbnailUrl: ''
          },
        ]);
      }
    };

    fetchAvailableDays();
  }, [schoolName]);

  const handleDateSearch = () => {
    if (selectedDate) {
      const found = availableDays.find(day => day.date === selectedDate);
      if (found) {
        onSelectDay(selectedDate);
      } else {
        alert('No hay fotos disponibles para esa fecha');
      }
    }
  };

  return (
    <div className="school-days">
      {/* Hero Section con imagen de fondo */}
      <div className="school-hero" style={{ backgroundImage: heroImage ? `url(${heroImage})` : 'none' }}>
        <div className="school-hero-overlay"></div>
        <div className="school-hero-content">
          <button className="btn-back-hero" onClick={onBack}>
            ← Volver
          </button>
          <h1 className="school-hero-title">{schoolName}</h1>
        </div>
      </div>

      <div className="school-days-content">
        {/* Búsqueda por fecha */}
        <div className="date-search-section">
          <h2>Buscar por fecha</h2>
          <div className="date-search-container">
            <input
              type="date"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              className="date-input"
            />
            <button 
              className="btn btn-primary"
              onClick={handleDateSearch}
              disabled={!selectedDate}
            >
              Buscar
            </button>
          </div>
        </div>

        {/* Listado de días */}
        <div className="days-section">
          <h2>Días disponibles</h2>
          <p className="days-subtitle">Selecciona un día para ver todas las fotos</p>

          <div className="days-grid">
            {availableDays.map((day) => (
              <div 
                key={day.date}
                className="day-card"
                onClick={() => onSelectDay(day.date)}
              >
                <div 
                  className="day-image-wrapper"
                  style={{ 
                    backgroundImage: day.thumbnailUrl ? `url(${day.thumbnailUrl})` : 'none'
                  }}
                >
                  <div className="day-image-overlay"></div>
                  <div className="day-image-info">
                    <span className="photo-count">{day.photoCount}</span>
                    <span className="photo-label">fotos</span>
                  </div>
                </div>
                <div className="day-info">
                  <h3 className="day-date">{day.displayDate}</h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SchoolDays;
