import React, { useState } from 'react';
import './Pricing.css';

const Pricing: React.FC = () => {
  const [expandedCard, setExpandedCard] = useState<number | null>(null);

  const pricingPlans = [
    {
      id: 1,
      title: 'Foto suelta',
      price: 15,
      description: 'Una imagen individual en alta calidad',
      details: [
        'Descarga inmediata',
        'Alta resolución (300 DPI)',
        'Sin marca de agua',
        'Formato JPG',
        'Válido para impresión',
      ],
    },
    {
      id: 2,
      title: 'Pack completo',
      price: 35,
      description: 'Todas las fotos de tu sesión, mismo día y persona',
      featured: true,
      details: [
        'Todas las fotos del día',
        'Descarga inmediata',
        'Alta resolución (300 DPI)',
        'Sin marca de agua',
        'Formato JPG',
        'Ahorra hasta 50%',
      ],
    },
    {
      id: 3,
      title: 'Día adicional',
      price: 20,
      description: 'Añade otro día a tu pack',
      details: [
        'Todas las fotos del día extra',
        'Mismo precio por día adicional',
        'Descarga inmediata',
        'Alta resolución',
        'Sin marca de agua',
      ],
    },
    {
      id: 4,
      title: 'Persona adicional',
      price: 20,
      description: 'Incluye otra persona en el mismo día',
      details: [
        'Fotos de otra persona',
        'Mismo día de sesión',
        'Descarga inmediata',
        'Alta resolución',
        'Sin marca de agua',
      ],
    },
  ];

  const toggleCard = (id: number) => {
    setExpandedCard(expandedCard === id ? null : id);
  };

  return (
    <section className="pricing" id="pricing">
      <div className="pricing-wrapper">
        <div className="pricing-header">
          <h2 className="pricing-title">Precios</h2>
          <p className="pricing-subtitle">
            Elige la opción que mejor se adapte a ti
          </p>
        </div>

        <div className="pricing-grid">
          {pricingPlans.map((plan) => (
            <div 
              key={plan.id} 
              className={`pricing-card ${plan.featured ? 'featured' : ''} ${expandedCard === plan.id ? 'expanded' : ''}`}
              onClick={() => toggleCard(plan.id)}
            >
              <div className="pricing-card-content">
                <h3 className="pricing-card-title">{plan.title}</h3>

                <div className="pricing-card-price">
                  <span className="price-amount">{plan.price}</span>
                  <span className="price-currency">€</span>
                </div>

                <p className="pricing-card-description">{plan.description}</p>

                {/* Detalles expandibles */}
                <div className={`pricing-card-details ${expandedCard === plan.id ? 'show' : ''}`}>
                  <div className="details-divider"></div>
                  <ul className="details-list">
                    {plan.details.map((detail, index) => (
                      <li key={index} className="detail-item">
                        <span className="detail-icon">✓</span>
                        <span className="detail-text">{detail}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Indicador de expansión */}
                <div className="expand-indicator">
                  <span className="expand-text">
                    {expandedCard === plan.id ? 'Ver menos' : 'Ver detalles'}
                  </span>
                  <span className={`expand-arrow ${expandedCard === plan.id ? 'rotated' : ''}`}>
                    ▼
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="pricing-note">
          <p>Todos los precios incluyen descarga inmediata sin marca de agua</p>
        </div>
      </div>
    </section>
  );
};

export default Pricing;
