import React, { useState, useEffect } from 'react';
import { adminApiService, Folder } from '../../services/adminApi';
import './AdminPage.css';

const PhotosPage: React.FC = () => {
  const [folders, setFolders] = useState<Folder[]>([]);
  const [selectedFolder, setSelectedFolder] = useState<string>('');
  const [files, setFiles] = useState<File[]>([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<string>('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadFolders();
  }, []);

  const loadFolders = async () => {
    try {
      setLoading(true);
      const data = await adminApiService.getFolders();
      setFolders(data);
      if (data.length > 0 && !selectedFolder) {
        setSelectedFolder(data[0].name);
      }
    } catch (err) {
      console.error('Error loading folders:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setFiles(Array.from(e.target.files));
    }
  };

  const handleUpload = async () => {
    if (!selectedFolder || files.length === 0) {
      alert('Selecciona una carpeta y archivos para subir');
      return;
    }

    try {
      setUploading(true);
      setUploadProgress(`Subiendo ${files.length} fotos...`);

      await adminApiService.uploadPhotos(selectedFolder, files);

      setUploadProgress('¡Fotos subidas exitosamente!');
      setFiles([]);

      setTimeout(() => {
        setUploadProgress('');
      }, 3000);
    } catch (err) {
      alert(err instanceof Error ? err.message : 'Error subiendo fotos');
      setUploadProgress('');
    } finally {
      setUploading(false);
    }
  };

  if (loading) {
    return (
      <div className="admin-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Cargando...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-container">
        <div className="admin-header">
          <h1>Subir Fotos</h1>
        </div>

        <div className="admin-content">
          {folders.length === 0 ? (
            <div className="alert alert-warning">
              No hay carpetas disponibles. Crea una carpeta primero.
            </div>
          ) : (
            <>
              <div className="form-group">
                <label className="form-label">Seleccionar Carpeta</label>
                <select
                  className="form-select"
                  value={selectedFolder}
                  onChange={(e) => setSelectedFolder(e.target.value)}
                >
                  {folders.map((folder) => (
                    <option key={folder.name} value={folder.name}>
                      {folder.name} ({folder.photo_count} fotos)
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Seleccionar Fotos</label>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleFileChange}
                  className="form-input"
                />
                {files.length > 0 && (
                  <p style={{ marginTop: '0.5rem', color: '#718096' }}>
                    {files.length} archivo(s) seleccionado(s)
                  </p>
                )}
              </div>

              {uploadProgress && (
                <div className="alert alert-info">
                  {uploadProgress}
                </div>
              )}

              <button
                onClick={handleUpload}
                disabled={uploading || files.length === 0}
                className="btn-primary"
              >
                {uploading ? 'Subiendo...' : 'Subir Fotos'}
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default PhotosPage;
